import React from "react";
import { gql, useQuery } from "@apollo/client";
import { Card, CardBody, CardTitle, CardSubtitle } from "reactstrap";
import { useState, useContext } from "react";
import AppContext from "./context";

function Drinks({ restId }) {
  const { addItem } = useContext(AppContext);

  const GET_RESTAURANT_DRINKS = gql`
  query($id: ID!) {
    restaurant(id: $id) {
      id
      name
      drinks {
        id
        price
        name
        description
        image {
          url
        }
      }
    }
  }
`;


  const { loading, error, data } = useQuery(GET_RESTAURANT_DRINKS, {
    variables: { id: restId },
  });

  if (loading) return <p>Loading drinks...</p>;
  if (error) return <p>Error: {error.message}</p>;
  if (!data) return <p>No drinks found</p>;

  let restaurant = data.restaurant;
  if (restId > 0){

    return (
      <>
          {restaurant.drinks.map((res) => (
            <Col xs="6" sm="4" style={{ padding: 0 }} key={res.id}>
              <Card style={{ margin: "0 10px" }}>
                <CardImg
                  top={true}
                  style={{ height: 150, width:150 }}
                  src={`http://localhost:1337${res.image.url}`}
                />
                <CardBody>
                  <CardTitle>{res.name}</CardTitle>
                  <CardText>{res.description}</CardText>
                  <CardText>{res.price}</CardText>
                </CardBody>
                <div className="card-footer">
                  <Button 
                    outline
                    color="primary"
                    onClick = {()=> addItem(res)}
                  >
                    + Add To Cart
                  </Button>
                  
                </div>
              </Card>
            </Col>
          ))}
        </>
        )}
        else{
          return <h1> No Drinks</h1>
        }
    }
export default Drinks;
