import { gql, useQuery } from '@apollo/client';
import Dishes from './dishes';
import Drinks from './drinks';
import AppContext from './context';
import { Button, Card, CardBody, CardImg, CardText, CardTitle, Container, Row, Col } from 'reactstrap';
import { useState, useContext, useEffect } from 'react';

function RestaurantList(props) {
  const [searchQuery, setSearchQuery] = useState(props.search);
  const [restaurantID, setRestaurantID] = useState(0);
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart);

  useEffect(() => {
    setSearchQuery(props.search);
  }, [props.search]);

  const GET_RESTAURANTS = gql`
  query($searchQuery: String) {
    restaurants(where: { 
      name_contains: $searchQuery,
      OR: [
        { dishes: { name_contains: $searchQuery } },
        { drinks: { name_contains: $searchQuery } }
      ]
    }) {
      id
      name
      description
      image {
        url
      }
    }
  }
`;

const { loading, error, data } = useQuery(GET_RESTAURANTS, {
  variables: { searchQuery },

},consoloe.log(error));


  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR</p>;
  if (!data) return <p>Not found</p>;

  const searchResults = data.restaurants.filter((res) => {
    return res.name.toLowerCase().includes(searchQuery);
  });

  const restId = searchResults.length > 0 ? searchResults[0].id : 0;

  // define renderer for Dishes
  const renderDishes = (restaurantID) => {
    return <Dishes restId={restaurantID}> </Dishes>;
  };
  const renderDrinks = (restaurantID) => {
    return <Drinks restId={restaurantID}> </Drinks>;
  };

  if (searchResults.length > 0) {
    const restList = searchResults.map((res) => (
      <Col xs='6' sm='4' key={res.id}>
        <Card style={{ margin: '0 0.5rem 20px 0.5rem' }}>
          <CardImg top={true} style={{ height: 200 }} src={`http://localhost:1337` + res.image.url} />
          <CardBody>
            <CardText>{res.description}</CardText>
          </CardBody>
          <div className='card-footer'>
            <Button color='info' onClick={() => setRestaurantID(res.id)}>
              {res.name}
            </Button>
          </div>
        </Card>
      </Col>
    ));

    return (
      <Container>
        <Row xs='3'>
          {restList}
          </Row>

        <Row xs='3'>
          {renderDishes(restaurantID)}
          </Row>
        <Row xs='3'>
        {renderDrinks(restaurantID)}
        </Row>

    </Container>

  )
} else {
  return <h1> No Restaurants Found</h1>
}
}
   export default RestaurantList;
