import { gql, useQuery } from '@apollo/client';
import Dishes from "./dishes"
import { useContext, useState } from 'react';
import Drinks from "./drinks";

import AppContext from "./context"

import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Container,
  Row,
  Col
} from "reactstrap";

function RestaurantList(props) {
  const [searchQuery, setSearchQuery] = useState(props.search);

  const [restaurantID, setRestaurantID] = useState(0)
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart)
  const GET_RESTAURANTS = gql`
  query {
    restaurants {
      id
      name
      description
      image {
        url
      }
      dishes {
        id
        name
        price
        image {
          url
        }
      }
      drinks {
        id
        name
        price
        image {
          url
        }
      }
    }
  }
`;
const [searchDishes, setSearchDishes] = useState("");
const [searchDrinks, setSearchDrinks] = useState("");

  const { loading, error, data } = useQuery(GET_RESTAURANTS)
  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR</p>;
  if (!data) return <p>Not found</p>;
  console.log(`Query Data: ${data.restaurants}`)

  // filter restaurants based on search query
  const filteredRestaurants = data.restaurants.filter((res) => {
    return res.name.toLowerCase().includes(props.search)
  })
  const filterDishes = (restaurant) => {
    if (!searchDishes) {
      return true;
    }
    const filteredDishes = restaurant.dishes.filter((dish) => {
      return dish.name.toLowerCase().includes(searchDishes.toLowerCase());
    });
    return filteredDishes.length > 0;
  };

  const filterDrinks = (restaurant) => {
    if (!searchDrinks) {
      return true;
    }
    const filteredDrinks = restaurant.drinks.filter((drink) => {
      return drink.name.toLowerCase().includes(searchDrinks.toLowerCase());
    });
    return filteredDrinks.length > 0;
  };

  const restId = searchQuery.length > 0 ? searchQuery[0].id : null;

  // define renderer for Dishes
  const renderDishes = (restaurantID) => {
    if (restaurantID) {
      const restaurant = data.restaurants.find((res) => res.id === restaurantID);
      const filteredDishes = restaurant.dishes.filter(filterDishes);
      if (filteredDishes.length > 0) {
        return <Dishes dishes={filteredDishes} />;
      } else {
        return <p>No dishes found.</p>;
      }
    } else {
      return null;
    }
  };

  const renderDrinks = (restaurantID) => {
    if (restaurantID) {
      const restaurant = data.restaurants.find((res) => res.id === restaurantID);
      const filteredDrinks = restaurant.drinks.filter(filterDrinks);
      if (filteredDrinks.length > 0) {
        return <Drinks drinks={filteredDrinks} />;
      } else {
        return <p>No drinks found.</p>;
      }
    } else {
      return null;
    }
  };

  // render all restaurants if there is no search query
  if (!props.search) {
    const restList = data.restaurants.map((res) => (
      <Col xs="6" sm="4" key={res.id}>
        <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
          <CardImg
            top={true}
            style={{ height: 200 }}
            src={
              `http://localhost:1337` + res.image.url
            }
          />
          <CardBody>
            <CardText>{res.description}</CardText>
          </CardBody>
          <div className="card-footer">
            <Button color="info" onClick={() => setRestaurantID(res.id)}>{res.name}</Button>
          </div>
        </Card>
      </Col>
    ))

    return (
      <Container>
        <Row xs='3'>
          {restList}
        </Row>

        <Row xs='3'>
          {renderDishes(restaurantID)}
        </Row>

        <Row xs='3'>
          {renderDrinks(restaurantID)}
        </Row>
      </Container>
    )
  } else if (filteredRestaurants.length > 0) {

    // render filtered restaurants if there is a search query
    const restList = filteredRestaurants.map((res) => (
      <Col xs="6" sm="4" key={res.id}>
        <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
          <CardImg
            top={true}
            style={{ height: 200 }}
            src={`http://localhost:1337${res.image.url}`}
          />
          <CardBody>
            <CardTitle tag="h5">{res.name}</CardTitle>
            <CardText>{res.description}</CardText>
          </CardBody>
          <div className="card-footer">
            <Button color="info" onClick={() => setRestaurantID(res.id)}>
              View Menu
            </Button>
          </div>
        </Card>
      </Col>
    ));
    return (
      <Container>
        <Row xs="3">{restList}</Row>
        <Row xs="3">{renderDishes(restaurantID)}</Row>
        <Row xs="3">{renderDrinks(restaurantID)}</Row>
      </Container>
    );
  } else {
    // render all restaurants if there is no search query
    const restList = data.restaurants.map((res) => (
      <Col xs="6" sm="4" key={res.id}>
        <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
          <CardImg
            top={true}
            style={{ height: 200 }}
            src={`http://localhost:1337${res.image.url}`}
          />
          <CardBody>
            <CardTitle tag="h5">{res.name}</CardTitle>
            <CardText>{res.description}</CardText>
          </CardBody>
          <div className="card-footer">
            <Button color="info" onClick={() => setRestaurantID(res.id)}>
              View Menu
            </Button>
          </div>
        </Card>
      </Col>
    ));
    return (
      <Container>
        <Row xs="3">{restList}</Row>
        <Row xs="3">{renderDishes(restaurantID)}</Row>
        <Row xs="3">{renderDrinks(restaurantID)}</Row>
      </Container>
    );
  }
};

export default RestaurantList;
