import React, { useState, useContext } from "react";
import { gql, useQuery } from "@apollo/client";
import Dishes from "./dishes";
import AppContext from "./context";
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Container,
  Row,
  Col,
} from "reactstrap";

function RestaurantList(props) {
  const [restaurantID, setRestaurantID] = useState(0);
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart);
  const GET_RESTAURANTS = gql`
    query {
      restaurants {
        id
        name
        description
        image {
          url
        }
        dishes {
          id
          name
          description
          price
        }
        drinks {
          id
          Name
          description
          price
        }
      }
    }
  `;
  const { loading, error, data } = useQuery(GET_RESTAURANTS);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR</p>;
  if (!data) return <p>Not found</p>;
  console.log(`Query Data: ${data.restaurants}`);

  let searchQuery = data.restaurants.map((res) => {
    if (res.id === restaurantID) {
      return {
        ...res,
        dishes: res.dishes.filter((dish) =>
          dish.name.toLowerCase().includes(props.search)
        ),
      };
    } else {
      return {
        ...res,
        dishes: res.dishes.filter((dish) =>
          dish.name.toLowerCase().includes(props.search)
        ),
        showDishes: false
      };
    }
  }).filter(res => res.dishes.length > 0);

  const restId = restaurantID;

  const handleRestaurantClick = (res) => {
    if (res.id === restaurantID) {
      setRestaurantID(0); // if already selected, deselect
    } else {
      setRestaurantID(res.id);
    }
  };

  // define renderer for Dishes
  const renderDishes = () => {
    if (restId === 0) return null;
    const restaurant = data.restaurants.find((res) => res.id === restId);
    if (!restaurant) return null;
    return (
      <>
        <Dishes restId={restId} />
        <h3>Drinks</h3>
        <Row>
          {restaurant.drinks.map((drink) => (
            <Col xs="6" sm="4" key={drink.id}>
              <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
                <CardImg
                  top={true}
                  style={{ height: 200 }}
                  src={`http://localhost:1337` + drink.image.url}
                />
                <CardBody>
                  <CardTitle>{drink.Name}</CardTitle>
                  <CardText>{drink.description}</CardText>
                  <CardText>${drink.price / 100}</CardText>
                  <Button
                    onClick={() =>
                      setState((prev) => {
                        const itemIndex = prev.findIndex(
                          (item) => item.id === drink.id && item.type === "drink"
                        );
                        if (itemIndex >= 0) {
                          const newState = [...prev];
                          newState[itemIndex].quantity++;
                          return newState;
                        } else {
                          return [
                            ...prev,
                            { id: drink.id, name: drink.Name, price: drink.price, quantity: 1, type: "drink" },
                          ];
                        }
                      })
                    }
                    color="primary"
                  >
                    Add to Cart
                  </Button>
                </CardBody>
              </Card>
            </Col>
          ))}
        </Row>
      </>
    );
  };


  const restList = searchQuery.map((res) => (
    <Col xs="6" sm="4" key={res.id}>
      <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
        <CardImg
          top={true}
          style={{ height: 200 }}
          src={`http://localhost:1337` + res.image.url}
        />
        <CardBody>
          <CardText>{res.description}</CardText>
        </CardBody>
        <div className="card-footer">
          <Button
            color={res.id === restaurantID ? "secondary" : "info"}
            onClick={() => handleRestaurantClick(res)}
          >
            {res.name}
          </Button>
        </div>
      </Card>
    </Col>
  ));

  return (
    <Container>
      <Row xs="3">{restList}</Row>
      <Row xs="3">{renderDishes()}</Row>
    </Container>
  );
}

export default RestaurantList;
