import React, { useState, useContext } from "react";
import { gql, useQuery } from "@apollo/client";
import Dishes from "./dishes";
import Drinks from "./drinks";
import AppContext from "./context";
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Col,
  Container,
  Row,
} from "reactstrap";

function RestaurantList(props) {
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart);
  const GET_RESTAURANTS = gql`
    query {
      restaurants {
        id
        name
        description
        image {
          url
        }
        dishes {
          id
          name
          description
          price
        }
        drinks {
          id
          price
          name
          description
        }
      }
    }
  `;
  const { loading, error, data } = useQuery(GET_RESTAURANTS);
  if (loading) return <p>Loading...</p>;
  if (error) {
    console.log(error);
    return <p>ERROR</p>;
  }
  if (!data) return <p>Not found</p>;

  const filteredRestaurants = data.restaurants.filter((restaurant) => {
    const hasFilteredDishes = restaurant.dishes.some((dish) => {
      return dish.name.toLowerCase().includes(props.search.toLowerCase());
    });
    const hasFilteredDrinks = restaurant.drinks.some((drink) => {
      return drink.name.toLowerCase().includes(props.search.toLowerCase());
    });

    return (
      hasFilteredDishes ||
      hasFilteredDrinks ||
      restaurant.name.toLowerCase().includes(props.search.toLowerCase())
    );
  });

  const handleRestaurantClick = (restaurant) => {
    setSelectedRestaurant(restaurant);
  };

  const renderDishes = () => {
    if (!selectedRestaurant) return null;
    return <Dishes restId={selectedRestaurant.id} />;
  };

  const renderDrinks = () => {
    if (!selectedRestaurant) return null;
    return <Drinks restId={selectedRestaurant.id} />;
  };

  const renderRestaurants = () => {
    if (filteredRestaurants.length === 0) {
      return <p>No results found.</p>;
    }

    return filteredRestaurants.map((restaurant) => {
      const hasFilteredDishes = restaurant.dishes.some((dish) => {
        return dish.name.toLowerCase().includes(props.search.toLowerCase());
      });
      const hasFilteredDrinks = restaurant.drinks.some((drink) => {
        return drink.name.toLowerCase().includes(props.search.toLowerCase());
      });
      const hasFilteredItems = hasFilteredDishes || hasFilteredDrinks;

      return (
        <Col xs="6" sm="4" key={restaurant.id}>
          <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
            <CardImg
              top={true}
              style={{ height: 200 }}
              src={`http://localhost:1337${restaurant.image.url}`}
            />
            <CardBody>
              <CardTitle>{restaurant.name}</CardTitle>
              <CardText>{restaurant.description}</CardText>
            </CardBody>
            <div className="card-footer">
              <Button
                color={
                  selectedRestaurant && selectedRestaurant.id === restaurant.id
                    ? "secondary"
                    : "info"
                }
                onClick={() => handleRestaurantClick(restaurant)}
              >
                {restaurant.name}
              </Button>
            </div>
            {hasFilteredItems && (
              <div>
                <Button
                  color={
                    selectedRestaurant &&
                    selectedRestaurant.id === restaurant.id &&
                    selectedRestaurant.showDishes &&
                    !selectedRestaurant.showDrinks
                      ? "secondary"
                      : "info"
                  }
                  onClick={() =>
                    setSelectedRestaurant({
                      ...selectedRestaurant,
                      showDishes: true,
                      showDrinks: false,
                    })
                  }
                  style={{ marginRight: "0.5rem", marginTop: "0.5rem" }}
                >
                  Dishes
                </Button>
                <Button
                  color={
                    selectedRestaurant &&
                    selectedRestaurant.id === restaurant.id &&
                    selectedRestaurant.showDrinks
                      ? "secondary"
                      : "info"
                  }
                  onClick={() =>
                    setSelectedRestaurant({
                      ...selectedRestaurant,
                      showDishes: false,
                      showDrinks: true,
                    })
                  }
                  style={{ marginTop: "0.5rem" }}
                >
                  Drinks
                </Button>
              </div>
            )}
          </Card>
        </Col>
      );
    });
  };

  const renderInventory = () => {
    if (!selectedRestaurant) return null;
    if (selectedRestaurant.showDishes) return renderDishes();
    if (selectedRestaurant.showDrinks) return renderDrinks();
    return null;
  };

  const updateState = (newState) => {
    setState(newState);
  };

  return (
    <Container style={{ marginTop: "20px" }}>
      <Row>

        <Col xs="12" sm="9">
          <h1 style={{ marginBottom: "20px" }}>Restaurants</h1>
          <Row>{renderRestaurants()}</Row>
           <Row>renderInventory()}</Row>
        </Col>
      </Row>
    </Container>
  );
}

export default RestaurantList;
