import React, { useState } from "react";
import { CardElement } from "@stripe/react-stripe-js";

function CardSection(props) {
  const [isLoading, setIsLoading] = useState(false);
  const [feedbackMessage, setFeedbackMessage] = useState("");
  const [paymentSuccessful, setPaymentSuccessful] = useState(false);

  const handleSubmit = async (event) => {
    // Prevent default form submission behavior
    event.preventDefault();

    // Disable submit button to prevent multiple submissions
    if (typeof props.setIsLoading === 'function') {
      props.setIsLoading(true);
    }

    // Send the payment method to Stripe
    const cardElement = props.elements && props.elements.getElement(CardElement);
    const { error, paymentMethod } = await props.stripe.createPaymentMethod({
      type: "card",
      card: cardElement,
    });

    // Re-enable submit button
    if (typeof props.setIsLoading === 'function') {
      props.setIsLoading(false);
    }

    if (error) {
      setFeedbackMessage(error.message);
      console.error(error);
    } else {
      setFeedbackMessage("Payment succeeded!");
      console.log(paymentMethod);
      setPaymentSuccessful(true);
    }
  };

  return (
    <div>
      <div>
        <label htmlFor="card-element">Credit or debit card</label>

        <div>
          <fieldset style={{ border: "none" }}>
            <div className="form-row">
              <div id="card-element" style={{ width: "100%" }}>
                <CardElement
                  options={{
                    style: { width: "100%", base: { fontSize: "18px" } },
                  }}
                />
              </div>
              <br />
              <div className="order-button-wrapper">
                <button onClick={handleSubmit}>Confirm order</button>
              </div>
              {props.stripeError ? (
                <div>{props.stripeError.toString()}</div>
              ) : null}
              {paymentSuccessful ? (
                <div className="feedback-message">Payment succeeded!</div>
              ) : (
                <div id="card-errors" role="alert" />
              )}
            </div>
          </fieldset>
        </div>
      </div>
      <style jsx>
        {`
          .order-button-wrapper {
            display: flex;
            width: 100%;
            align-items: flex-end;
            justify-content: flex-end;
          }

          .feedback-message {
            margin-top: 1em;
            font-weight: bold;
          }
        `}
      </style>
    </div>
  );
}

export default CardSection;
