import React, { useState, useContext } from "react";
import { gql, useQuery } from "@apollo/client";
import Dishes from "./dishes";
import Drinks from "./drinks";
import AppContext from "./context";
import { Button, Card, CardBody, CardImg, CardText, CardTitle, Col, Container, Row } from 'reactstrap';

function RestaurantList(props) {
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart);
  const GET_RESTAURANTS = gql`
    query {
      restaurants {
        id
        name
        description
        image {
          url
        }
        dishes {
          id
          name
          description
          price
        }
        drinks {
          id
          price
          name
          description

        }
      }
    }
  `;
  const { loading, error, data } = useQuery(GET_RESTAURANTS);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);

  if (loading) return <p>Loading...</p>;
  if (error) {console.log(error); return <p>ERROR</p>;}
  if (!data) return <p>Not found</p>;
  console.log(`Query Data: ${data.restaurants}`);

  const handleRestaurantClick = (restaurant) => {
    if (selectedRestaurant && selectedRestaurant.id === restaurant.id) {
      setSelectedRestaurant(null);
    } else {
      setSelectedRestaurant(restaurant);
    }
  };

  const searchQuery = data.restaurants.filter((restaurant) =>
    restaurant.dishes.some(
      (dish) => dish.name.toLowerCase().includes(props.search.toLowerCase())
    ) ||
    restaurant.drinks.some(
      (drink) => drink.name.toLowerCase().includes(props.search.toLowerCase())
    )
  );

  let firstMatchingRestaurant = null;
  for (let i = 0; i < searchQuery.length; i++) {
    const restaurant = searchQuery[i];
    const matchedDishes = restaurant.dishes.filter((dish) =>
      dish.name.toLowerCase().includes(props.search.toLowerCase())
    );
    const matchedDrinks = restaurant.drinks.filter((drink) =>
      drink.name.toLowerCase().includes(props.search.toLowerCase())
    );
    if (matchedDishes.length > 0 || matchedDrinks.length > 0) {
      firstMatchingRestaurant = restaurant;
      break;
    }
  }

  const restList = searchQuery.map((restaurant) => {
    const matchedDishes = restaurant.dishes.filter((dish) =>
      dish.name.toLowerCase().includes(props.search.toLowerCase())
    );
    const matchedDrinks = restaurant.drinks.filter((drink) =>
      drink.name.toLowerCase().includes(props.search.toLowerCase())
    );
    return (
      <Col xs="6" sm="4" key={restaurant.id}>
        <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
          <CardImg
            top={true}
            style={{width: "100%", height: "14rem", objectFit: "cover" }}
src={restaurant.image.url}
alt={`${restaurant.name} image`}
/>
<CardBody>
<CardTitle tag="h5">{restaurant.name}</CardTitle>
<CardText>{restaurant.description}</CardText>
<Button
color="info"
onClick={() => handleRestaurantClick(restaurant)}
>
{selectedRestaurant && selectedRestaurant.id === restaurant.id
? "Hide Menu"
: "View Menu"}
</Button>
</CardBody>
</Card>
{selectedRestaurant && selectedRestaurant.id === restaurant.id && (
<div>
<hr />
<h4>Dishes</h4>
<Dishes dishes={matchedDishes} restaurant={restaurant} />
<h4>Drinks</h4>
<Drinks drinks={matchedDrinks} restaurant={restaurant} />
</div>
)}
</Col>
);
});

return (
<Container style={{ marginTop: "2rem" }}>
<Row>
{firstMatchingRestaurant ? (
<div>
<h3>Best Match</h3>
<Col xs="12" sm="6" style={{ margin: "0.5rem" }}>
<Card>
<CardImg
top={true}
style={{
width: "100%",
height: "14rem",
objectFit: "cover",
}}
src={firstMatchingRestaurant.image.url}
alt={`${firstMatchingRestaurant.name} image`}
/>
<CardBody>
<CardTitle tag="h5">
{firstMatchingRestaurant.name}
</CardTitle>
<CardText>
{firstMatchingRestaurant.description}
</CardText>
<Button
color="info"
onClick={() =>
handleRestaurantClick(firstMatchingRestaurant)
}
>
{selectedRestaurant &&
selectedRestaurant.id === firstMatchingRestaurant.id
? "Hide Menu"
: "View Menu"}
</Button>
</CardBody>
</Card>
{selectedRestaurant &&
selectedRestaurant.id === firstMatchingRestaurant.id && (
<div>
<hr />
<h4>Dishes</h4>
<Dishes
                   dishes={firstMatchingRestaurant.dishes}
                   restaurant={firstMatchingRestaurant}
                 />
<h4>Drinks</h4>
<Drinks
                   drinks={firstMatchingRestaurant.drinks}
                   restaurant={firstMatchingRestaurant}
                 />
</div>
)}
</Col>
</div>
) : null}
{restList}
</Row>
</Container>
);
}

export default RestaurantList;
