import React, { useState, useContext } from "react";
import { gql, useQuery } from "@apollo/client";
import Dishes from "./dishes";

import AppContext from "./context";
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Container,
  Row,
  Col,
} from "reactstrap";

function RestaurantList(props) {
  const [restaurantID, setRestaurantID] = useState(0);
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart);
  const GET_RESTAURANTS = gql`
    query {
      restaurants {
        id
        name
        description
        image {
          url
        }
        dishes {
          id
          name
          description
          price
        }
      }
    }
  `;
  const { loading, error, data } = useQuery(GET_RESTAURANTS);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR</p>;
  if (!data) return <p>Not found</p>;
  console.log(`Query Data: ${data.restaurants}`);

  let searchQuery = data.restaurants.filter((res) => {
    return (
      res.name.toLowerCase().includes(props.search) ||
      res.dishes.some((dish) => dish.name.toLowerCase().includes(props.search))
    );
  });

  let restId = restaurantID;

  // define renderer for Dishes
  const renderDishes = () => {
    if (restId === 0) return null;
    const restaurant = data.restaurants.find((res) => res.id === restId);
    if (!restaurant) return null;
    if (restaurant.id !== restaurantID) return null;
    return <Dishes dishes={restaurant.dishes}></Dishes>;
  };

  // define handler for restaurant button click
  const handleRestaurantClick = (id) => {
    if (id === restaurantID) {
      setRestaurantID(0); // hide dishes if same restaurant button is clicked
    } else {
      setRestaurantID(id); // show dishes for selected restaurant
    }
    console.log(handleRestaurantClick)
  };

  const restList = searchQuery.map((res) => (
    <Col xs="6" sm="4" key={res.id}>
      <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
        <CardImg
          top={true}
          style={{ height: 200 }}
          src={`http://localhost:1337` + res.image.url}
        />
        <CardBody>
          <CardText>{res.description}</CardText>
        </CardBody>
        <div className="card-footer">
          <Button color="info" onClick={() => handleRestaurantClick(res.id)}>
            {res.name}
          </Button>
        </div>
      </Card>
    </Col>
  ));

  return (
    <Container>
      <Row xs="3">{restList}</Row>

      <Row xs="3">{renderDishes()}</Row>
    </Container>
  );
}

export default RestaurantList;
