import React, { useState, useContext } from "react";
import { gql, useQuery } from "@apollo/client";
import Dishes from "./dishes";
import Drinks from "./drinks";
import AppContext from "./context";
import { Button, Card, CardBody, CardImg, CardText, CardTitle, Col, Container, Row } from 'reactstrap';

function RestaurantList(props) {
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart);
  const GET_RESTAURANTS = gql`
    query {
      restaurants {
        id
        name
        description
        image {
          url
        }
        dishes {
          id
          name
          description
          price
        }
        drinks {
          id
          price
          name
          description

        }
      }
    }
  `;
  const { loading, error, data } = useQuery(GET_RESTAURANTS);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);

  if (loading) return <p>Loading...</p>;
  if (error) {console.log(error); return <p>ERROR</p>;}
  if (!data) return <p>Not found</p>;
  console.log(`Query Data: ${data.restaurants}`);

  const handleRestaurantClick = (restaurant) => {
    if (selectedRestaurant && selectedRestaurant.id === restaurant.id) {
      setSelectedRestaurant(null);
    } else {
      setSelectedRestaurant(restaurant);
    }
  };


  const renderDishes = () => {
    if (!selectedRestaurant) return null;
    const filteredDishes = selectedRestaurant.dishes.filter((dish) =>
      dish.name.toLowerCase().includes(props.search)
    );
    return <Dishes dishes={filteredDishes} restId={selectedRestaurant.id} />;
  };

  const renderDrinks = () => {
    if (!selectedRestaurant) return null;
    const filteredDrinks = selectedRestaurant.drinks.filter((drink) =>
      drink.name.toLowerCase().includes(props.search)
    );
    return <Drinks drinks={filteredDrinks} restId={selectedRestaurant.id} />;
  };


  const restList = data.restaurants.map((restaurant) => {
    const matchedDishes = restaurant.dishes.filter((dish) =>
      dish.name.toLowerCase().includes(props.search)
    );
    const matchedDrinks = restaurant.drinks.filter((drink) =>
      drink.name.toLowerCase().includes(props.search)
    );
    if (matchedDishes.length > 0) {
      return (
        <Col xs="6" sm="4" key={restaurant.id}>
          <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
            <CardBody>
              <CardTitle>{matchedDishes[0].name}</CardTitle>
              <CardText>{matchedDishes[0].description}</CardText>
            </CardBody>
          </Card>
        </Col>
      );
    } else if (matchedDrinks.length > 0) {
      return (
        <Col xs="6" sm="4" key={restaurant.id}>
          <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
            <CardBody>
              <CardTitle>{matchedDrinks[0].name}</CardTitle>
              <CardText>{matchedDrinks[0].description}</CardText>
            </CardBody>
          </Card>
        </Col>
      );
    }
    return null;
  });

  return (
    <Container>
      <Row xs="3">{restList}</Row>
      <Row xs="3">{renderDishes()}</Row>
      <Row xs="3">{renderDrinks()}</Row>
    </Container>
  );
}

export default RestaurantList;
