import React, { useState, useEffect } from "react";
import { getUserFromLocalStorage } from "../components/auth";

function Receipts() {
  const [orders, setOrders] = useState([]);
  const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";

  useEffect(() => {
    const fetchData = async () => {
      try {
        const user = getUserFromLocalStorage();
        if (!user) {
          console.log("User not found in local storage");
          return;
        }

        const response = await fetch(`${API_URL}/orders?user=${user.id}`, {
          headers: {
            Authorization: `Bearer ${user.jwt}`,
          },
        });

        const data = await response.json();
        setOrders(data);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1 style={{ fontSize: "2rem", marginBottom: "1rem" }}>Order History</h1>
      {orders.map((order) => (
        <div key={order.id} style={{ 
            border: "1px solid #ccc", 
            padding: "1rem", marginBottom: index === orders.length - 1 ? 0 : "1rem",}}>
          <p>Order ID: {order.id}</p>
          <p>Amount: ${order.amount}</p>
          <p>Status: {order.status}</p>
        </div>
      ))}
    </div>
  );
}

export default Receipts;
