import { AppProps } from 'next/app';
import { Component } from 'react';
import Head from 'next/head';

import AppContext, { AppState, AppAction } from '../context/AppContext';
import { useReducer, useEffect } from 'react';
import Cookie from 'js-cookie';


function MyApp(props) {
  const { cart, addItem, removeItem, user, setUser } = useContext(AppContext);
  const [state, setState] = useState({ cart: cart, user: user });

  const handleSetUser = (user) => {
    setState({ ...state, user: user, isAuthenticated: !!user });
  };

  const handleAddItem = (item) => {
    let { items } = state.cart;

    //check for item already in cart
    let foundItem = items.find((i) => i.id === item.id);

    if (foundItem) {
      // if item already exists in cart, increase quantity
      let newCart = {
        items: items.map((item) => {
          if (item.id === foundItem.id) {
            return { ...item, quantity: item.quantity + 1 };
          } else {
            return item;
          }
        }),
        total: state.cart.total + item.price,
      };

      setState({ cart: newCart, user: state.user });
    } else {
      // if item is not in cart, add it
      let newCart = {
        items: [...state.cart.items, { ...item, quantity: 1 }],
        total: state.cart.total + item.price,
      };

      setState({ cart: newCart, user: state.user });
    }
  };

  const handleRemoveItem = (item) => {
    let { items } = state.cart;
    //check for item already in cart
    const foundItem = items.find((i) => i.id === item.id);
    if (foundItem.quantity > 1) {
      var newCart = {
        items: items.map((item) => {
          if (item.id === foundItem.id) {
            return Object.assign({}, item, { quantity: item.quantity - 1 });
          } else {
            return item;
          }
        }),
        total: state.cart.total - item.price,
      };
      //console.log(`NewCart after remove: ${JSON.stringify(newCart)}`)
    } else {
      // only 1 in the cart so remove the whole item
      console.log(`Try remove item ${JSON.stringify(foundItem)}`);
      const index = items.findIndex((i) => i.id === foundItem.id);
      items.splice(index, 1);
      var newCart = { items: items, total: state.cart.total - item.price };
    }
    setState({ cart: newCart, user: state.user });
  };

  // Save user session and cart data to cookies whenever the state changes
  useEffect(() => {
    Cookie.set("user", JSON.stringify(state.user));
    Cookie.set("cart", JSON.stringify(state.cart));
  }, [state]);

  // Restore user session and cart data from cookies when the app loads
  useEffect(() => {
    const userFromCookie = Cookie.get("user");
    const cartFromCookie = Cookie.get("cart");
    if (userFromCookie) {
      setUser(JSON.parse(userFromCookie));
    }

    if (cartFromCookie) {
      setState((prevState) => ({
        ...prevState,
        cart: JSON.parse(cartFromCookie),
      }));
    }
  }, []);

  return (
    <AppContext.Provider
      value={{
        cart: state.cart,
        addItem: handleAddItem,
        removeItem: handleRemoveItem,
        isAuthenticated: !!state.user,
        user: state.user,
        setUser: handleSetUser,
      }}
    >
      <Head>
        <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css"
          integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm"
          crossOrigin="anonymous"
        />
      </Head>
      <Layout>
        <Component {...pageProps} />
      </Layout>
    </AppContext.Provider>
  );
}

export default MyApp;
