import React, { useState, useContext } from "react";
import { gql, useQuery } from "@apollo/client";
import Dishes from "./dishes";
import Drinks from "./drinks";
import AppContext from "./context";
import { Button, Card, CardBody, CardImg, CardText, CardTitle, Col, Container, Row } from 'reactstrap';

function RestaurantList(props) {
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart);
  const GET_RESTAURANTS = gql`
    query {
      restaurants {
        id
        name
        description
        image {
          url
        }
        dishes {
          id
          name
          description
          price
        }
        drinks {
          id
          price
          name
          description
        }
      }
    }
  `;
  const { loading, error, data } = useQuery(GET_RESTAURANTS);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);

  if (loading) return <p>Loading...</p>;
  if (error) {
    console.log(error);
    return <p>ERROR</p>;
  }
  if (!data) return <p>Not found</p>;
  console.log(`Query Data: ${data.restaurants}`);
  const handleRestaurantClick = (restaurant) => {
    if (selectedRestaurant && selectedRestaurant.id === restaurant.id) {
      setSelectedRestaurant(null);
    } else {
      setSelectedRestaurant(restaurant);
    }
  };

  const renderDishes = () => {
    if (!selectedRestaurant) return null;
    const matchedDishes = selectedRestaurant.dishes.filter((dish) =>
      dish.name.toLowerCase().includes(props.search.toLowerCase())
    );
    if (matchedDishes.length === 0) return null;

    const relatedDishes = selectedRestaurant.dishes.filter((dish) =>
      dish.name.toLowerCase().includes(props.search.toLowerCase()) === false && 
      dish.type === matchedDishes[0].type
    );

    const renderDishes = () => {
      if (!selectedRestaurant) return null;
      const matchedDishes = selectedRestaurant.dishes.filter((dish) =>
        dish.name.toLowerCase().includes(props.search.toLowerCase())
      );
      if (matchedDishes.length === 0) return null;

      // Get all dishes with the same type as the matched dish
      const matchedType = matchedDishes[0].type;
      const matchedTypeDishes = selectedRestaurant.dishes.filter((dish) =>
        dish.type === matchedType && !dish.name.toLowerCase().includes(props.search.toLowerCase())
      );

      return (
        <>
          <Dishes
            dishes={matchedDishes}
            restId={selectedRestaurant.id}
          />
          {matchedTypeDishes.length > 0 && (
            <>
              <h3>Related Dishes</h3>
              <Dishes
                dishes={matchedTypeDishes}
                restId={selectedRestaurant.id}
              />
            </>
          )}
        </>
      );
    };

    const renderDrinks = () => {
      if (!selectedRestaurant) return null;
      const matchedDrinks = selectedRestaurant.drinks.filter((drink) =>
        drink.name.toLowerCase().includes(props.search.toLowerCase())
      );
      if (matchedDrinks.length === 0) return null;

      // Get all drinks with the same type as the matched drink
      const matchedType = matchedDrinks[0].type;
      const matchedTypeDrinks = selectedRestaurant.drinks.filter((drink) =>
        drink.type === matchedType && !drink.name.toLowerCase().includes(props.search.toLowerCase())
      );

      return (
        <>
          <Drinks
            drinks={matchedDrinks}
            restId={selectedRestaurant.id}
          />
          {matchedTypeDrinks.length > 0 && (
            <>
              <h3>Related Drinks</h3>
              <Drinks
                drinks={matchedTypeDrinks}
                restId={selectedRestaurant.id}
              />
            </>
          )}
        </>
      );
    };


    return (
      <>
        <Drinks drinks={matchedDrinks} restId={selectedRestaurant.id} />
        {relatedDrinks.length > 0 && (
          <>
            <h3>Related Drinks</h3>
            <Drinks drinks={relatedDrinks} restId={selectedRestaurant.id} />
          </>
        )}
      </>
    );
  };



  const searchQuery = data.restaurants.filter((restaurant) => {
    const matchedDishes = restaurant.dishes.filter((dish) =>
      dish.name.toLowerCase().includes(props.search.toLowerCase())
    );
    const matchedDrinks = restaurant.drinks.filter((drink) =>
      drink.name.toLowerCase().includes(props.search.toLowerCase())
    );
    const matchedRestaurant = restaurant.name.toLowerCase().includes(props.search.toLowerCase());
    return matchedDishes.length > 0 || matchedDrinks.length > 0 || matchedRestaurant;
  });



  const restList = searchQuery.map((restaurant) => (
    <Col xs="6" sm="4" key={restaurant.id}>
      <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
        <CardImg
          top={true}
          style={{ height: 200 }}
          src={`http://localhost:1337` + restaurant.image.url}
        />
        <CardBody>
          <CardTitle>{restaurant.name}</CardTitle>
          <CardText>{restaurant.description}</CardText>
        </CardBody>
        <div className="card-footer">
          <Button
            color={
              selectedRestaurant && selectedRestaurant.id === restaurant.id
                ? "secondary"
                : "info"
            }
            onClick={() => handleRestaurantClick(restaurant)}
          >
            {selectedRestaurant && selectedRestaurant.id === restaurant.id
              ? "Close"
              : "View Menu"}
          </Button>
        </div>
      </Card>
    </Col>
  ));

  return (
    <Container>
      <Row xs="3">{restList}</Row>
      <Row xs="3">{renderDishes()}</Row>
      <Row xs="3">{renderDrinks()}</Row>
    </Container>
  );
}

export default RestaurantList;
