import React, { useState } from "react";
import { Card, CardImg, CardText, CardBody, CardTitle, Button, Col, Row, Input } from "reactstrap";
import { useQuery } from "@apollo/client";
import { RESTAURANTS_QUERY } from "../graphql/queries";

const RestaurantList = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const { loading, error, data } = useQuery(RESTAURANTS_QUERY);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  const handleSearch = (event) => {
    setSearchTerm(event.target.value.toLowerCase());
  };

  const searchQuery = data.restaurants.filter((restaurant) => {
    const matchedDishes = restaurant.dishes.filter((dish) =>
      dish.name.toLowerCase().includes(searchTerm)
    );
    const matchedDrinks = restaurant.drinks.filter((drink) =>
      drink.name.toLowerCase().includes(searchTerm)
    );
    const matchedRestaurant = restaurant.name.toLowerCase().includes(searchTerm);

    return matchedDishes.length > 0 || matchedDrinks.length > 0 || matchedRestaurant;
  });

  const restList = searchQuery.map((restaurant) => {
    const matchedDishes = restaurant.dishes.filter((dish) =>
      dish.name.toLowerCase().includes(searchTerm)
    );
    const matchedDrinks = restaurant.drinks.filter((drink) =>
      drink.name.toLowerCase().includes(searchTerm)
    );

    return (
      <Col xs="6" sm="4" key={restaurant.id}>
        <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
          <CardImg top={true} style={{ height: 200 }} src={`http://localhost:1337${restaurant.image.url}`} />
          <CardBody>
            <CardTitle>{restaurant.name}</CardTitle>
            <CardText>{restaurant.description}</CardText>
            {matchedDishes.length > 0 && (
              <div>
                <h5>Dishes:</h5>
                <ul>
                  {matchedDishes.map((dish) => (
                    <li key={dish.id}>
                      {dish.name} - ${dish.price}
                    </li>
                  ))}
                </ul>
              </div>
            )}
            {matchedDrinks.length > 0 && (
              <div>
                <h5>Drinks:</h5>
                <ul>
                  {matchedDrinks.map((drink) => (
                    <li key={drink.id}>
                      {drink.name} - ${drink.price}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </CardBody>
          <div className="card-footer">
            <Button
              color={selectedRestaurant && selectedRestaurant.id === restaurant.id ? "secondary" : "info"}
              onClick={() => setSelectedRestaurant(restaurant)}
            >
              View Details
            </Button>
          </div>
        </Card>
      </Col>
    );
  });

  return (
    <>
      <Row style={{ marginBottom: "20px" }}>
        <Col>
          <Input type="text" placeholder="Search" onChange={handleSearch} />
        </Col>
      </Row>
      <Row>{restList}</Row>
    </>
  );
};

export default RestaurantList;
