import React, { useState, useContext } from "react";
import { gql, useQuery } from "@apollo/client";
import Dishes from "./dishes";
import Drinks from "./drinks";
import AppContext from "./context";
import { Button, Card, CardBody, CardImg, CardText, CardTitle, Col, Container, Row } from 'reactstrap';

function RestaurantList(props) {
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart);
  const GET_RESTAURANTS = gql`
    query {
      restaurants {
        id
        name
        description
        image {
          url
        }
        dishes {
          id
          name
          description
          price
        }
        drinks {
          id
          price
          name
          description
        }
      }
    }
  `;
  const { loading, error, data } = useQuery(GET_RESTAURANTS);
  if (loading) return <p>Loading...</p>;
  if (error) {console.log(error); return <p>ERROR</p>;}
  if (!data) return <p>Not found</p>;

  const searchQuery = data.restaurants.map((restaurant) => {
    const filteredDishes = restaurant.dishes.filter((dish) => {
      return dish.name.toLowerCase().includes(props.search.toLowerCase());
    });
    const filteredDrinks = restaurant.drinks.filter((drink) => {
      return drink.name.toLowerCase().includes(props.search.toLowerCase());
    });

    return {
      ...restaurant,
      dishes: filteredDishes,
      drinks: filteredDrinks,
    }
  });

  const handleRestaurantClick = (restaurant) => {
    setSelectedRestaurant(restaurant);
  };

  const renderDishes = () => {
    if (!selectedRestaurant) return null;
    return <Dishes restId={selectedRestaurant.id} />;
  };

  const renderDrinks = () => {
    if (!selectedRestaurant) return null;
    return <Drinks restId={selectedRestaurant.id} />;
  };

  const renderRestaurants = () => {
    return searchQuery.map((restaurant) => {
      const hasFilteredItems = restaurant.dishes.length > 0 || restaurant.drinks.length > 0;

      return (
        <Col xs="6" sm="4" key={restaurant.id}>
          <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
            <CardImg
              top={true}
              style={{ height: 200 }}
              src={`http://localhost:1337${restaurant.image.url}`}
            />
            <CardBody>
              <CardTitle>{restaurant.name}</CardTitle>
              <CardText>{restaurant.description}</CardText>
            </CardBody>
            <div className="card-footer">
              <Button
                color={selectedRestaurant && selectedRestaurant.id === restaurant.id ? "secondary" : "info"}
                onClick={() => handleRestaurantClick(restaurant)}
              >
                {restaurant.name}
              </Button>
            </div>
            {hasFilteredItems && (
              <div>
                <Button
                  color={selectedRestaurant && selectedRestaurant.id === restaurant.id && selectedRestaurant.showDishes ? "secondary" : "info"}
                  onClick={() => setSelectedRestaurant({...selectedRestaurant, showDishes: !selectedRestaurant.showDishes})}
                >
                  Show Drinks
                </Button>
              </div>
            )}
          </Card>
        </Col>
      );
    });
  };

  return (
    <div>
      <Container fluid>
        <Row xs="3"> 

            {renderRestaurants()}


            {renderDishes()}
            {renderDrinks()}

        </Row>
      </Container>
    </div>
  );
}

export default RestaurantList;
