import { gql, useQuery } from '@apollo/client';
import Dishes from "./dishes"
import { useContext, useState } from 'react';
import AppContext from "./context"
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Container,
  Row,
  Col
} from "reactstrap";

function RestaurantList(props) {
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart);
  const [restaurantID, setRestaurantID] = useState(0);

  const GET_RESTAURANTS = gql`
    query {
      restaurants {
        id
        name
        description
        image {
          url
        }
        dishes {
          id
          name
          price
        }
      }
    }
  `;

  const { loading, error, data } = useQuery(GET_RESTAURANTS);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR</p>;
  if (!data) return <p>Not found</p>;

  const restaurants = data.restaurants.filter((res) => {
    return res.name.toLowerCase().includes(props.search);
  });

  const renderDishes = (restaurantID) => {
    const restaurant = data.restaurants.find((res) => res.id === restaurantID);
    if (restaurant && restaurant.dishes.length > 0) {
      return <Dishes dishes={restaurant.dishes} />;
    }
    return <h2>No dishes found for this restaurant</h2>;
  };

  const renderRestaurants = () => {
    if (restaurants.length === 0) {
      return <h2>No restaurants found</h2>;
    }
    return restaurants.map((res) => (
      <Col xs="6" sm="4" key={res.id}>
        <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
          <CardImg
            top={true}
            style={{ height: 200 }}
            src={`http://localhost:1337${res.image.url}`}
          />
          <CardBody>
            <CardTitle>{res.name}</CardTitle>
            <CardText>{res.description}</CardText>
          </CardBody>
          <div className="card-footer">
            <Button color="info" onClick={() => setRestaurantID(res.id)}>
              View dishes
            </Button>
          </div>
        </Card>
      </Col>
    ));
  };

  return (
    <Container>
      <Row xs="3">{renderRestaurants()}</Row>
      <Row xs="3">{renderDishes(restaurantID)}</Row>
    </Container>
  );
}

export default RestaurantList;
