import React, { useState, useContext } from "react";
import { gql, useQuery } from "@apollo/client";
import Dishes from "./dishes";
import Drinks from "./drinks";
import AppContext from "./context";
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Container,
  Row,
  Col,
} from "reactstrap";

function RestaurantList(props) {
  const [restaurantID, setRestaurantID] = useState(0);
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart);
  const GET_RESTAURANTS = gql`
    query {
      restaurants {
        id
        name
        description
        image {
          url
        }
        dishes {
          id
          name
          description
          price
        }
        drink {
          id
          name
          description
          price
        }
      }
    }
  `;
  const { loading, error, data } = useQuery(GET_RESTAURANTS);
if (loading) return <p>Loading...</p>;
if (error) {
  console.log(error.message);
  return <p>ERROR</p>;
}
if (!data) return <p>Not found</p>;
console.log(`Query Data: ${data.restaurants}`);


  let searchQuery = data.restaurants.map((res) => {
    if (res.id === restaurantID) {
      return {
        ...res,
        dishes: res.dishes.filter((dish) =>
          dish.name.toLowerCase().includes(props.search)
        ),
        drinks: res.drinks.filter((drink) => // filter drinks by search query
          drink.name.toLowerCase().includes(props.search)
        ),
      };
    } else {
      return {
        ...res,
        dishes: res.dishes.filter((dish) =>
          dish.name.toLowerCase().includes(props.search)
        ),
        drinks: res.drinks.filter((drink) =>
          drink.name.toLowerCase().includes(props.search)
        ),
        showDishes: false,
      };
    }
  }).filter(res => res.dishes.length > 0 || res.drinks.length > 0);

  const restId = restaurantID;

  const handleRestaurantClick = (res) => {
    if (res.id === restaurantID) {
      setRestaurantID(0); // if already selected, deselect
    } else {
      setRestaurantID(res.id);
    }
  };

  const renderDishes = () => {
    if (restId === 0) return null;
    const restaurant = data.restaurants.find((res) => res.id === restId);
    if (!restaurant || !restaurant.dishes) return null;
    return (
      <div>
        <Dishes restId={restId} data={restaurant.dishes} />
      </div>
    );
  };

  const renderDrinks = () => {
    if (restId === 0) return null;
    const restaurant = data.restaurants.find((res) => res.id === restId);
    if (!restaurant || !restaurant.drinks) return null;
    return (
      <div>
        <Drinks restId={restId} data={restaurant.drinks} />
      </div>
    );
  };


  const restList = searchQuery.map((res) => (
    <Col xs="6" sm="4" key={res.id}>
      <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
        <CardImg
          top={true}
          style={{ height: 200 }}
          src={`http://localhost:1337` + res.image.url}
        />
        <CardBody>
          <CardText>{res.description}</CardText>
        </CardBody>
        <div className="card-footer">
          <Button
            color={res.id === restaurantID ? "secondary" : "info"}
            onClick={() => handleRestaurantClick(res)}
          >
            {res.name}
          </Button>
        </div>
      </Card>
      {res.showDishes ? <Dishes restId={res.id} dishes={res.dishes} /> : null}
      {res.showDrinks ? <Drinks restId={res.id} drinks={res.drinks} /> : null}
    </Col>
  ));



return (
  <div>
    <Row>{restList}</Row>
    <Row>{renderDishes}</Row>
    <Row>{renderDrinks}</Row>
  </div>
);}
export default RestaurantList;
