import React from "react";
import { gql, useQuery } from "@apollo/client";
import { Card, CardBody, CardTitle, CardSubtitle } from "reactstrap";
import { useState, useContext } from "react";
import AppContext from "./context";
function Drinks(props) {
    const GET_DRINKS = gql`
      query {
        drinks {
          id
          name
          description
          price
          restaurant {
            id
          }
        }
      }
    `;
  
    const { loading, error, data } = useQuery(GET_DRINKS);
  
    if (loading) return <p>Loading drinks...</p>;
  
    if (error) return <p>Error: {error.message}</p>;
  
    if (!data || !data.drinks || !data.drinks.length)
      return <p>No drinks found for this restaurant.</p>;
  
    const drinksData = data.drinks.filter((drink) => drink.restaurant.id === props.restId);
  
    const drinksList = drinksData.map((drink) => (
      <Card key={drink.id}>
        <CardBody>
          <CardTitle tag="h5">{drink.name}</CardTitle>
          <CardSubtitle tag="h6" className="mb-2 text-muted">
            {drink.description}
          </CardSubtitle>
          <CardSubtitle tag="h6" className="mb-2 text-muted">
            {drink.price}
          </CardSubtitle>
        </CardBody>
      </Card>
    ));
  
    return <div>{drinksList}</div>;
  };

export default Drinks;
